mapper.select:
select
from contract_counterpart WHERE is_deleted = 0 and unified_social_credit_code in
#{code}

mapper.insert:
insert into contract_counterpart_setting
(
contract_id,
counterpart_id
)
values

(
#{contractId},
#{item.id}
)

mapper.delete:
DELETE
FROM contract_counterpart
WHERE id NOT IN (
    SELECT t.max_id
    FROM (SELECT max(id) AS max_id from contract_counterpart group by unified_social_credit_code) as t
);

DELETE
FROM contract_counterpart
WHERE unified_social_credit_code IN (NULL,'null','');
